#menuViewContent {
    list-style: none;
    margin-bottom: 10px;
    float: left;
    width: 100%;
    position: relative;
    z-index: 5;
    margin-top: 0;
    font-size: 15px;
    margin-top: 3px;
    padding:0px;
}

    #menuViewContent li {
        float: left;
        margin-right: 10px;
        position: relative;
    }

    #menuViewContent a {
        display: block;
        padding: 5px;
        color: #666666;
        text-decoration: none;
    }

        #menuViewContent a:hover {
            color: #003a5d;
            background: white;
            text-decoration: underline;
        }

    #menuViewContent ul {
        background: white;
        list-style: none;
        position: absolute;
        left: -9999px;
        padding-left: 3px;
        -moz-border-radius-topleft: 0px;
        -moz-border-radius-topright: 0px;
        -moz-border-radius-bottomleft: 6px;
        -moz-border-radius-bottomright: 6px;
        -webkit-border-top-left-radius: 0px;
        -webkit-border-top-right-radius: 0px;
        -webkit-border-bottom-left-radius: 6px;
        -webkit-border-bottom-right-radius: 6px;
        border-top-left-radius: 0px;
        border-top-right-radius: 0px;
        border-bottom-left-radius: 6px;
        border-bottom-right-radius: 6px;
        -moz-box-shadow: 1px 1px 5px #363536;
        -webkit-box-shadow: 1px 1px 5px #363536;
        box-shadow: 1px 1px 5px #363536;
    }

        #menuViewContent ul li {
            padding-top: 1px;
            float: none;
        }

        #menuViewContent ul a {
            white-space: nowrap;
        }

    #menuViewContent li:hover ul {
        left: 0;
    }

    #menuViewContent li:hover a {
        text-decoration: underline;
    }

    #menuViewContent li:hover ul a {
        text-decoration: none;
    }

    #menuViewContent li:hover ul li a:hover {
        text-decoration: underline;
        background: white;
    }

    #menuViewContent a.selected {
        color: #003a5d;
    }

